import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router, RouterModule } from '@angular/router';
import { MatToolbarModule } from '@angular/material/toolbar';
import { MatButtonModule } from '@angular/material/button';
import { MatCardModule } from '@angular/material/card';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatIconModule } from '@angular/material/icon';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { MatSnackBarModule, MatSnackBar } from '@angular/material/snack-bar';
import { UserService } from '../../../services/user.service';
import { AuthService } from '../../../services/auth.service';

@Component({
  selector: 'app-usuario-form',
  standalone: true,
  imports: [
    CommonModule, 
    ReactiveFormsModule, 
    RouterModule,
    MatToolbarModule,
    MatButtonModule,
    MatCardModule,
    MatFormFieldModule,
    MatInputModule,
    MatIconModule,
    MatProgressSpinnerModule,
    MatSnackBarModule
  ],
  template: `
    <div class="app-container">
      <mat-toolbar color="primary" class="app-toolbar">
        <button mat-icon-button routerLink="/usuarios">
          <mat-icon>arrow_back</mat-icon>
        </button>
        <span>Novo Usuário</span>
        <span class="spacer"></span>
        <button mat-button (click)="logout()">
          <mat-icon>logout</mat-icon>
          Sair
        </button>
      </mat-toolbar>

      <div class="content">
        <mat-card class="form-card">
          <mat-card-header>
            <mat-card-title>Criar Novo Usuário</mat-card-title>
            <mat-card-subtitle>Preencha os dados do usuário</mat-card-subtitle>
          </mat-card-header>

          <mat-card-content>
            <form [formGroup]="userForm" (ngSubmit)="onSubmit()" class="user-form">
              <div class="form-row">
                <mat-form-field appearance="outline" class="full-width">
                  <mat-label>Nome</mat-label>
                  <input
                    matInput
                    type="text"
                    formControlName="name"
                    placeholder="Digite o nome do usuário"
                  />
                  <mat-icon matSuffix>person</mat-icon>
                  <mat-error *ngIf="userForm.get('name')?.invalid && userForm.get('name')?.touched">
                    Nome é obrigatório
                  </mat-error>
                </mat-form-field>
              </div>

              <div class="form-row">
                <mat-form-field appearance="outline" class="full-width">
                  <mat-label>Email</mat-label>
                  <input
                    matInput
                    type="email"
                    formControlName="email"
                    placeholder="Digite o email do usuário"
                  />
                  <mat-icon matSuffix>email</mat-icon>
                  <mat-error *ngIf="userForm.get('email')?.invalid && userForm.get('email')?.touched">
                    Email é obrigatório e deve ser válido
                  </mat-error>
                </mat-form-field>
              </div>

              <div class="form-row">
                <mat-form-field appearance="outline" class="full-width">
                  <mat-label>Senha</mat-label>
                  <input
                    matInput
                    type="password"
                    formControlName="password"
                    placeholder="Digite a senha do usuário"
                  />
                  <mat-icon matSuffix>lock</mat-icon>
                  <mat-error *ngIf="userForm.get('password')?.invalid && userForm.get('password')?.touched">
                    Senha é obrigatória e deve ter pelo menos 6 caracteres
                  </mat-error>
                </mat-form-field>
              </div>

              <div class="form-row">
                <mat-form-field appearance="outline" class="full-width">
                  <mat-label>Confirmar Senha</mat-label>
                  <input
                    matInput
                    type="password"
                    formControlName="confirmPassword"
                    placeholder="Confirme a senha"
                  />
                  <mat-icon matSuffix>lock</mat-icon>
                  <mat-error *ngIf="userForm.get('confirmPassword')?.invalid && userForm.get('confirmPassword')?.touched">
                    Confirmação de senha é obrigatória
                  </mat-error>
                  <mat-error *ngIf="userForm.hasError('passwordMismatch') && userForm.get('confirmPassword')?.touched">
                    As senhas não coincidem
                  </mat-error>
                </mat-form-field>
              </div>

              <div *ngIf="errorMessage" class="error-message">
                <mat-icon color="warn">error</mat-icon>
                <span>{{ errorMessage }}</span>
              </div>

              <div *ngIf="successMessage" class="success-message">
                <mat-icon color="primary">check_circle</mat-icon>
                <span>{{ successMessage }}</span>
              </div>
            </form>
          </mat-card-content>

          <mat-card-actions class="form-actions">
            <button
              mat-raised-button
              color="primary"
              [disabled]="userForm.invalid || loading"
              (click)="onSubmit()"
            >
              <mat-spinner *ngIf="loading" diameter="20" style="margin-right: 8px;"></mat-spinner>
              <mat-icon *ngIf="!loading">person_add</mat-icon>
              {{ loading ? 'Criando...' : 'Criar Usuário' }}
            </button>

            <button
              mat-button
              (click)="cancel()"
              [disabled]="loading"
            >
              <mat-icon>cancel</mat-icon>
              Cancelar
            </button>
          </mat-card-actions>
        </mat-card>
      </div>
    </div>
  `,
