export class UsuarioFormComponent implements OnInit {
  userForm: FormGroup;
  loading = false;
  errorMessage = '';
  successMessage = '';

  constructor(
    private fb: FormBuilder,
    private userService: UserService,
    private authService: AuthService,
    private router: Router,
    private snackBar: MatSnackBar
  ) {
    this.userForm = this.fb.group({
      name: ['', [Validators.required]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      confirmPassword: ['', [Validators.required]]
    }, { validators: this.passwordMatchValidator });
  }

  ngOnInit() {
    // Verificar se o usuário está autenticado e é admin
    if (!this.authService.isAuthenticated()) {
      this.router.navigate(['/login']);
      return;
    }

    if (!this.authService.isAdmin()) {
      this.snackBar.open('Apenas administradores podem criar usuários', 'Fechar', {
        duration: 5000,
        horizontalPosition: 'center',
        verticalPosition: 'top'
      });
      this.router.navigate(['/usuarios']);
    }
  }

  passwordMatchValidator(form: FormGroup) {
    const password = form.get('password');
    const confirmPassword = form.get('confirmPassword');

    if (password && confirmPassword && password.value !== confirmPassword.value) {
      return { passwordMismatch: true };
    }

    return null;
  }

  onSubmit() {
    if (this.userForm.valid) {
      this.loading = true;
      this.errorMessage = '';
      this.successMessage = '';

      const userData = {
        name: this.userForm.value.name,
        email: this.userForm.value.email,
        password: this.userForm.value.password
      };

      this.userService.createUser(userData).subscribe({
        next: (user) => {
          this.loading = false;
          this.successMessage = 'Usuário criado com sucesso!';
          this.userForm.reset();

          this.snackBar.open('Usuário criado com sucesso!', 'Fechar', {
            duration: 3000,
            horizontalPosition: 'center',
            verticalPosition: 'top'
          });

          // Redirecionar após 2 segundos
          setTimeout(() => {
            this.router.navigate(['/usuarios']);
          }, 2000);
        },
        error: (error) => {
          this.loading = false;
          this.errorMessage = 'Erro ao criar usuário. Verifique os dados e tente novamente.';
          this.snackBar.open('Erro ao criar usuário. Verifique os dados e tente novamente.', 'Fechar', {
            duration: 5000,
            horizontalPosition: 'center',
            verticalPosition: 'top'
          });
          console.error('Erro ao criar usuário:', error);
        }
      });
    }
  }

  cancel() {
    this.router.navigate(['/usuarios']);
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
